import { Box, Button, TextField, Typography, List, ListItem } from '@mui/material';
import { useState } from 'react';
import { informationItems } from '../mocks/_mockInformation';

const InformationPage = () => {
    const [searchValue, setSearchValue] = useState('');
    const [searchResults, setSearchResults] = useState<string[]>(informationItems);

    const handleSearch = () => {
        const filteredInfo = informationItems.filter((item: any) => item.title.includes(searchValue) || item.information.includes(searchValue));

        setSearchResults(filteredInfo);
    };

    const handleKeyDown = (event: any) => {
        if (event.key === 'Enter') {
            handleSearch();
        }
    };

    return (
        <Box sx={{ display: "flex", border: '1px solid black', height: "100vh", marginTop: "24px" }}>
            <Box sx={{ flex: "25%", display: "flex", flexDirection: "row" }}>
                <TextField
                    value={searchValue}
                    sx={{ height: "20px", flexGrow: 1 }}
                    fullWidth
                    onChange={(event) => setSearchValue(event?.target.value)}
                    onKeyDown={handleKeyDown}
                />
                <Button sx={{ height: "20px"}} variant="contained" onClick={handleSearch}>
                    Search
                </Button>
            </Box>
            <Box sx={{ flex: "75%" }}>
                {searchValue ? (
                    searchResults.length > 0 ?
                    <List>
                        {searchResults.map((item, index) => (
                            <ListItem key={index}>
                                <Typography>{item}</Typography>
                            </ListItem>
                        ))}
                    </List> :
                    <Typography>No results found</Typography>
                ) : informationItems.map((item, index) => (
                    <ListItem key={index}>
                        <Typography>{item}</Typography>
                    </ListItem>
                ))}
            </Box>
        </Box>
    );
}

export default InformationPage;
